INSERT INTO `ServiceMapping` VALUES ('Air_quality_monitoring_station','1',100,NULL,'SELECT ?NO2 ?SO2 ?H2S ?CO ?Benzene ?PM2_5 ?PM10 ?annualPM10ExceedCount ?measuredTime ?icqa ?icqaAnnualPM10ExceedCount ?icqaCombinedCounter ?icqaMeasuredTime {\n?s a km4c:AirQualityObservation.\n?s km4c:measuredBySensor <%SERVICE_URI>.\n?s km4c:measuredTime/dcterms:identifier ?measuredTime.\nOPTIONAL {?s km4c:airQualityNO2 ?NO2}\nOPTIONAL {?s km4c:airQualitySO2 ?SO2}\nOPTIONAL {?s km4c:airQualityH2S ?H2S}\nOPTIONAL {?s km4c:airQualityCO ?CO}\nOPTIONAL {?s km4c:airQualityPM10 ?PM10}\nOPTIONAL {?s km4c:airQualityPM2.5 ?PM2_5}\nOPTIONAL {?s km4c:airQualityBenzene ?Benzene}\nOPTIONAL {?s km4c:annualPM10ExceedanceCount ?annualPM10ExceedCount}\nOPTIONAL {\nselect ?icqa ?icqaAnnualPM10ExceedCount ?icqaCombinedCounter ?icqaMeasuredTime {\n?i km4c:hasMonitoringStation <%SERVICE_URI>.\n?i km4c:hasObservation ?o.\n?o km4c:measuredTime/dcterms:identifier ?icqaMeasuredTime.\n?o km4c:airQualityCombinedCounter ?icqaCombinedCounter;\n   km4c:airQualityICQAValue ?icqa;\n   km4c:annualPM10ExceedanceCount ?icqaAnnualPM10ExceedCount \n} order by desc(?icqaMeasuredTime) limit 1\n}\n} \nORDER BY DESC(?measuredTime)\nLIMIT 1\n',NULL,NULL,NULL),('Bike_rack','1',100,NULL,'SELECT ?availableBikes ?freeStalls ?brokenBikes ?measuredTime {\n?s a km4c:BikeRackObservation.\n?s km4c:measuredBySensor <%SERVICE_URI>.\n?s km4c:measuredTime/dcterms:identifier ?measuredTime.\nOPTIONAL {?s km4c:availableBikes ?availableBikes}\nOPTIONAL {?s km4c:freeStalls ?freeStalls}\nOPTIONAL {?s km4c:brokenBikes ?brokenBikes}\n} \nORDER BY DESC(?measuredTime)\nLIMIT 1',NULL,NULL,NULL),('Car_park','1',100,NULL,NULL,NULL,'select datePrediction as \"datePrediction\",freePrediction as \"freePrediction\" from carParkPrediction where serviceuri=\'%SERVICE_URI\' order by actualdate desc, dateprediction limit 4','select daytrend as \"day\", hourtrend as \"hour\", freetrend as \"free\" from carParkTrend where serviceuri=\'%SERVICE_URI\' and actu